userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* linkhint boxes */
  background: -webkit-gradient(
    linear,
    left top,
    left bottom,
    color-stop(0%, #fff785),
    color-stop(100%, #ffc542)
  );
  border: 1px solid #e3be23;
}

div > .vimiumHintMarker span {
  /* linkhint text */
  font-size: 12px;
}

/*****************************************************************************/
/*                                 my styles                                 */
/*****************************************************************************/

iframe.vimium-hud-frame {
  right: 10px;
}

#hud-container {
  width: inherit;
  border-radius: 8px;
  right: 0;
  left: auto;
  bottom: 17px;
  padding: 2px;
}

#hud {
  height: auto;
  padding: 0;
}

#search-area {
  padding: 0;
}

#search-area > #hud {
  font-size: 13px;
  margin-bottom: -2px;
}

span#hud-find-input:before {
  content: "/";
  margin: 0 1px;
}
